import React, { useState, useEffect } from "react";
import ReactQuill from "react-quill";
import { Button, Typography, Card, message, Select } from "antd";
import axios from "axios";
import "react-quill/dist/quill.snow.css";

const { Title } = Typography;
const { Option } = Select;

const DEFAULT_TEMPLATE = `<p class="ql-align-center"><img src="data:image/png;base64,"></p><p><br></p><p>Dear <strong>[Customer Name]</strong>,</p><p><br></p><p>We’ve curated something special for you! Based on your recent purchases and browsing history, here are some exclusive offers and recommendations we think you'll love.</p><h4><br></h4><h4>Personalized Product Recommendations:</h4><ul><li><strong>[Product Name 1]</strong> - Price: $[Product Price] - Discount: [Discount Percentage]% off with code [Promo Code]</li><li><strong>[Product Name 2]</strong> - Price: $[Product Price] - Discount: Buy one, get one Free</li><li><strong>[Product Name 3]</strong> - Price: $[Product Price] - Discount: Save [Discount Amount] when you buy with [Related Product]</li></ul><p><br></p><p>Take advantage of these personalized offers and discover more with <strong>Timperio</strong>. Shop now and enjoy the best deals tailored just for you!</p><p>Warm regards,</p><p><strong>Marketing Team</strong></p><p><br></p><p><br></p><p class="ql-align-center"><br></p><p class="ql-align-center">Visit us at <strong>www.timperio.co</strong></p><p class="ql-align-center"><strong>Italy </strong>C.so Vittorio Emanuele, 19 86044 Colletorto (CB) ITALY</p><p class="ql-align-center"><strong>Singapore </strong>21 Woodlands Close PRIMZ Bizhub, #02-28 Singapore 737854</p><p><br></p><p class="ql-align-center">Copyright @ 2024 Timperio Snc</p>`

export const NewsLetter = () => {
    
  const DRAFT_KEY = "newsletter_draft";
  const [content, setContent] = useState("");
  const [isDraftLoaded, setIsDraftLoaded] = useState(false);
  const [recipients, setRecipients] = useState([]); // State to hold selected recipients
  const [emailList, setEmailList] = useState([
    "customer1@example.com",
    "customer2@example.com",
    "customer3@example.com",
  ]); // Placeholder email list

  // Load saved draft from localStorage on component mount
  useEffect(() => {
    if (!isDraftLoaded) {
      const savedDraft = localStorage.getItem(DRAFT_KEY);
      if (savedDraft) {
        setContent(savedDraft);
        message.info("Draft loaded from localStorage.");
      } else {
        setContent(DEFAULT_TEMPLATE);
      }
      setIsDraftLoaded(true);
    }
  }, [isDraftLoaded]);

  // Save draft to localStorage
  const handleSaveAsDraft = () => {
    localStorage.setItem(DRAFT_KEY, content);
    message.success("Draft saved successfully!");
  };

  const handleResetTemplate = () => {
    setContent(DEFAULT_TEMPLATE);
  }
  // Handle email sending
  const handleSend = async () => {
    if (!recipients.length) {
      message.error("Please select at least one recipient.");
      return;
    }

    try {
      // Placeholder Axios POST request
      const response = await axios.post("/api/send-email", {
        recipients,
        content,
      });
      console.log("Email sent response:", response.data);
      message.success("Emails sent successfully!");
    } catch (error) {
      console.error("Error sending email:", error);
      message.error("Failed to send emails.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <Title level={2}>Email Template Editor</Title>
      <div style={{ marginBottom: "20px" }}>
        <Title level={5}>Additional Recipients (Optional):</Title>
        <Select
          mode="multiple"
          placeholder="Select recipients"
          style={{ width: "100%" }}
          value={recipients}
          onChange={setRecipients}
        >
          {emailList.map((email) => (
            <Option key={email} value={email}>
              {email}
            </Option>
          ))}
        </Select>
      </div>
      <Card
        style={{
          borderRadius: "10px",
          marginBottom: "20px",
        }}
      >
        <ReactQuill
          value={content}
          onChange={setContent}
          modules={{
            toolbar: [
              [{ header: [1, 2, false] }],
              ["bold", "italic", "underline", "strike"],
              [{ list: "ordered" }, { list: "bullet" }],
              [{ align: [] }],
              ["link", "image"],
              ["clean"],
            ],
          }}
          formats={[
            "header",
            "bold",
            "italic",
            "underline",
            "strike",
            "list",
            "bullet",
            "align",
            "link",
            "image",
          ]}
          style={{ minHeight: "200px" }}
        />
      </Card>
      <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
        <Button color="danger" variant="outlined" onClick={handleResetTemplate}>
            Reset
        </Button>
        <Button type="default" onClick={handleSaveAsDraft}>
          Save as Draft
        </Button>
        <Button type="primary" onClick={handleSend}>
          Send
        </Button>
      </div>
    </div>
  );
};
